CREATE OR REPLACE FUNCTION qua_obter_qt_partic_trein ( nr_seq_treinamento_p bigint, ie_faltou_p text) RETURNS bigint AS $body$
DECLARE


qt_participante_w	bigint := 0;

BEGIN
if (coalesce(nr_seq_treinamento_p,0) > 0) then
	begin
	if (coalesce(ie_faltou_p,'A') = 'A') then
		select	count(*)
		into STRICT	qt_participante_w
		from	qua_doc_trein_pessoa
		where	nr_seq_treinamento 	= nr_seq_treinamento_p;
	else
		select	count(*)
		into STRICT	qt_participante_w
		from	qua_doc_trein_pessoa
		where	nr_seq_treinamento 	= nr_seq_treinamento_p
		and	ie_faltou 			= ie_faltou_p;
	end if;
	end;
end if;

return	qt_participante_w;

end;
$body$
