SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION qua_obter_qt_trein_pend_confir ( cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w			bigint;


BEGIN

select	count(*)
into STRICT	ds_retorno_w
from	qua_doc_trein_pessoa b,
	qua_doc_treinamento a
where	a.nr_sequencia = b.nr_seq_treinamento
and 	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and	coalesce(b.dt_confirmacao::text, '') = ''
and	coalesce(b.nm_usuario_confirmacao::text, '') = ''
and	b.ie_faltou = 'N'
and	b.cd_pessoa_fisica = cd_pessoa_fisica_p
and	trunc(coalesce(a.dt_real,clock_timestamp())) >= to_date('08/07/2011','dd/mm/yy');

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
