CREATE OR REPLACE FUNCTION qua_obter_resp_analise_probl ( nr_seq_problema_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


qt_total_w		bigint;
ds_retorno_w		varchar(255);
ds_resposta_w		varchar(255);
ie_abrir_rnc_w		varchar(15);
nr_seq_resposta_w		bigint;

/*
ie_opcao_p
R - Resposta
A - Abrir RNC - Dom2582
*/
BEGIN

select	qua_obter_total_analise_probl(nr_seq_problema_p)
into STRICT	qt_total_w
;

select	max(nr_sequencia)
into STRICT	nr_seq_resposta_w
from	qua_resposta_problema
where	vl_resposta_min <= qt_total_w
and	vl_resposta_max >= qt_total_w
and	cd_estabelecimento = cd_estabelecimento_p;

select	ds_resposta,
	ie_abrir_rnc
into STRICT	ds_resposta_w,
	ie_abrir_rnc_w
from	qua_resposta_problema
where	nr_sequencia = nr_seq_resposta_w;

if (ie_opcao_p = 'R') then
	ds_retorno_w := ds_resposta_w;
elsif (ie_opcao_p = 'A') then
	ds_retorno_w := ie_abrir_rnc_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
