CREATE OR REPLACE FUNCTION qua_obter_se_classificacao_lib (nr_seq_classificacao_p bigint, nm_usuario_P text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1):= 'S';
qt_regras_w		integer;
cd_estabelecimento_w	smallint:= wheb_usuario_pck.get_cd_estabelecimento;


BEGIN

select	count(*)
into STRICT	qt_regras_w
from	qua_nc_classificacao_estab
where	nr_seq_classificacao = nr_seq_classificacao_p;

if (qt_regras_w > 0) then
	begin
	select	'S'
	into STRICT	ds_retorno_w
	from	qua_nc_classificacao_estab
	where	nr_seq_classificacao	= nr_seq_classificacao_p
	and	cd_estabelecimento		= cd_estabelecimento_w  LIMIT 1;
	exception
	when others then
		ds_retorno_w := 'N';
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
