CREATE OR REPLACE FUNCTION qua_obter_se_disp_item_lib ( nr_seq_tipo_disp_item_p bigint, cd_setor_resp_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1):= 'S';
qt_regras_w		integer;
cd_estabelecimento_w	smallint:= wheb_usuario_pck.get_cd_estabelecimento;


BEGIN
select	count(*)
into STRICT	qt_regras_w
from	qua_nc_tipo_disp_it_estab
where	nr_seq_tipo_disp_item = nr_seq_tipo_disp_item_p;

if (qt_regras_w > 0) then
	begin
	select	'S'
	into STRICT	ds_retorno_w
	from	qua_nc_tipo_disp_it_estab
	where	nr_seq_tipo_disp_item 	= nr_seq_tipo_disp_item_p
	and	cd_estabelecimento 	= cd_estabelecimento_w  LIMIT 1;
	exception
	when others then
		ds_retorno_w := 'N';
	end;
end if;

if (coalesce(cd_setor_resp_p,0) <> 0) and (ds_retorno_w = 'S') then
	begin
	select	count(*)
	into STRICT	qt_regras_w
	from	qua_nc_tipo_disp_it_setor
	where	nr_seq_tipo_disp_item = nr_seq_tipo_disp_item_p;

	if (qt_regras_w > 0) then
		begin
		select	'S'
		into STRICT	ds_retorno_w
		from	qua_nc_tipo_disp_it_setor
		where	nr_seq_tipo_disp_item = nr_seq_tipo_disp_item_p
		and	cd_setor_atendimento  = cd_setor_resp_p  LIMIT 1;
		exception
		when others then
			ds_retorno_w := 'N';
		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
