CREATE OR REPLACE FUNCTION qua_obter_se_evento_lib ( nr_seq_evento_p bigint, cd_setor_paciente_p bigint default null) RETURNS varchar AS $body$
DECLARE

			
cd_perfil_w				bigint;
cd_setor_atendimento_w	integer;
qt_reg_w				bigint;
ie_retorno_w			varchar(10);
cd_estabelecimento_w	bigint;
cd_setor_paciente_w		bigint;


BEGIN
cd_perfil_w		:= wheb_usuario_pck.get_cd_perfil;--obter_perfil_ativo;
cd_setor_atendimento_w	:= obter_setor_ativo;
cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;
cd_setor_paciente_w		:= coalesce(cd_setor_paciente_p,0);

ie_retorno_w			:= 'S';
select	coalesce(max(1), 0)
into STRICT	qt_reg_w
from	qua_evento_liberacao
where	nr_seq_evento	= nr_seq_evento_p;

if (qt_reg_w	> 0) then
	select	coalesce(max(1), 0)
	into STRICT	qt_reg_w
	from	qua_evento_liberacao
	where	nr_seq_evento = nr_seq_evento_p
	and	coalesce(cd_perfil,cd_perfil_w) 				= cd_perfil_w
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_w)	= cd_setor_atendimento_w
	and	coalesce(cd_estabelecimento,cd_estabelecimento_w) 		= cd_estabelecimento_w
	and	coalesce(cd_setor_paciente,cd_setor_paciente_w)			= cd_setor_paciente_w;
	
	if (qt_reg_w	= 0) then
		ie_retorno_w	:= 'N';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
