SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION qua_obter_se_gravid_evento_lib ( nr_seq_gravidade_p bigint, ie_classificacao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';
qt_existe_w		bigint;
nr_seq_classif_w		bigint;


BEGIN

if (coalesce(nr_seq_gravidade_p,0) > 0) and (coalesce(ie_classificacao_p,'0') <> '0') then
	begin
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_classif_w
	from	qua_classif_evento
	where	cd_empresa = coalesce(obter_empresa_estab(wheb_usuario_pck.get_cd_estabelecimento),cd_empresa)
	and	ie_classificacao = ie_classificacao_p;

	select	count(*)
	into STRICT	qt_existe_w
	from	qua_grav_evento_classif
	where	ie_situacao = 'A'
	and	nr_seq_gravidade = nr_seq_gravidade_p;

	if (nr_seq_classif_w > 0) and (qt_existe_w > 0) then
		begin
		select	'S'
		into STRICT	ds_retorno_w
		from	qua_grav_evento_classif
		where	nr_seq_gravidade = nr_seq_gravidade_p
		and	nr_seq_classificacao = nr_seq_classif_w
		and	ie_situacao = 'A'  LIMIT 1;
		exception
		when others then
			ds_retorno_w := 'N';
		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
