SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION qua_obter_se_lidos ( nr_sequencia_p bigint, nm_usuario_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w            bigint := 0;
dt_documento_w		timestamp;
dt_acesso_w		timestamp;
/*ie_opcao_p:
S = se leu o documento
N = se n√£o leu o documento
*/
BEGIN

if (ie_opcao_p = 'N') then
        select  max(y.dt_atualizacao)
        into STRICT    dt_acesso_w
        from    qua_doc_log_acesso y
        where   y.nr_seq_doc = nr_sequencia_p
        and     y.nm_usuario = nm_usuario_p;

	select  max(x.dt_atualizacao)
        into STRICT    dt_documento_w
        from    qua_documento x
        where   (x.dt_aprovacao IS NOT NULL AND x.dt_aprovacao::text <> '')
        and     (x.dt_validacao IS NOT NULL AND x.dt_validacao::text <> '')
        and     x.nr_sequencia = nr_sequencia_p;

	if (dt_documento_w > dt_acesso_w) then
		vl_retorno_w := 1;
	end if;


elsif (ie_opcao_p = 'S') then
        select  max(y.dt_atualizacao)
        into STRICT    dt_acesso_w
        from    qua_doc_log_acesso y
        where   y.nr_seq_doc = nr_sequencia_p
        and     y.nm_usuario = nm_usuario_p;

	select  max(x.dt_atualizacao)
        into STRICT    dt_documento_w
        from    qua_documento x
        where   (x.dt_aprovacao IS NOT NULL AND x.dt_aprovacao::text <> '')
        and     (x.dt_validacao IS NOT NULL AND x.dt_validacao::text <> '')
        and     x.nr_sequencia = nr_sequencia_p;

	if (dt_documento_w <= dt_acesso_w) then
		vl_retorno_w := 1;
	end if;
end if;

return vl_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
