CREATE OR REPLACE FUNCTION qua_obter_se_pessoa_inativ_doc ( nr_seq_documento_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(01) := 'S';
nr_seq_tipo_w		bigint;
ie_regra_tipo_doc_w	varchar(01);
ie_regra_tipo_doc_pf_w	varchar(01);
ie_regra_partic_doc_w	varchar(01);
ie_regra_partic_doc_pf_w	varchar(01);


BEGIN
if (nr_seq_documento_p > 0) then
	begin

	select	nr_seq_tipo
	into STRICT	nr_seq_tipo_w
	from	qua_documento
	where	nr_sequencia = nr_seq_documento_p;

	begin
	select	'S'
	into STRICT	ie_regra_tipo_doc_w
	from	qua_tipo_doc_inativ
	where	nr_seq_tipo_doc 	= nr_seq_tipo_w  LIMIT 1;
	exception
	when others then
		ie_regra_tipo_doc_w := 'N';
	end;

	begin
	select	'S'
	into STRICT	ie_regra_tipo_doc_pf_w
	from	qua_tipo_doc_inativ
	where	nr_seq_tipo_doc 	= nr_seq_tipo_w
	and	cd_pessoa_fisica 	= cd_pessoa_fisica_p  LIMIT 1;
	exception
	when others then
		ie_regra_tipo_doc_pf_w := 'N';
	end;

	begin
	select	'S'
	into STRICT	ie_regra_partic_doc_w
	from	qua_doc_participante
	where	nr_seq_doc	= nr_seq_documento_p
	and	cd_participante 	= cd_pessoa_fisica_p  LIMIT 1;
	exception
	when others then
		ie_regra_partic_doc_w := 'N';
	end;

	begin
	select	'S'
	into STRICT	ie_regra_partic_doc_pf_w
	from	qua_doc_participante
	where	ie_tipo = 'I'
	and	nr_seq_doc	= nr_seq_documento_p
	and	cd_participante 	= cd_pessoa_fisica_p  LIMIT 1;
	exception
	when others then
		ie_regra_partic_doc_pf_w := 'N';
	end;

	/*se não tem nenhuma regra pra ninguém, permite*/

	if (ie_regra_tipo_doc_w = 'N') and (ie_regra_partic_doc_w = 'N') then
		ds_retorno_w := 'S';

	/*Se não tem regra por tipo, mas tem por participante, verifica*/

	elsif (ie_regra_tipo_doc_w = 'N') and (ie_regra_partic_doc_w = 'S') then
		ds_retorno_w := ie_regra_partic_doc_pf_w;

	/*se tem regra por tipo, verifica*/

	elsif (ie_regra_tipo_doc_w = 'S') then
		begin
		/*Se não tem por participante, considera a regra por tipo*/

		if (ie_regra_partic_doc_w = 'N') then
			ds_retorno_w := ie_regra_tipo_doc_pf_w;

		/*Se tem por tipo e por participante, prioridade por participante*/

		elsif (ie_regra_partic_doc_w = 'S') then
			begin
			if (ie_regra_tipo_doc_pf_w = 'S') or (ie_regra_partic_doc_pf_w = 'S') then
				ds_retorno_w := 'S';
			else
				ds_retorno_w := 'N';
			end if;
			end;
		end if;
		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
