CREATE OR REPLACE FUNCTION qua_obter_se_pessoa_trein ( nr_sequencia_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(01) := 'N';


/*
ie_opcao_p
	1 - Pessoa treinada
	2 - Resp. treinamento
*/
BEGIN
if (ie_opcao_p = '1') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	qua_doc_trein_pessoa
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	nr_seq_treinamento	= nr_sequencia_p;
elsif (ie_opcao_p = '2') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	qua_doc_trein_resp
	where	cd_pessoa_resp 	= cd_pessoa_fisica_p
	and	nr_seq_treinamento	= nr_sequencia_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
