SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION qua_obter_se_processo_doc_lib ( nr_sequencia_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'S';
qt_reg_w		bigint;
cd_perfil_w	usuario_perfil.cd_perfil%Type := Obter_perfil_Ativo;


BEGIN

select	count(1)
into STRICT	qt_reg_w
from	qua_processo_lib
where	nr_seq_processo = nr_sequencia_p;

if (qt_reg_w > 0) then
	begin
	ie_retorno_w := 'N';

	select	'S'
	into STRICT	ie_retorno_w
	from	qua_processo_lib b,
		qua_processo a
	where	a.nr_sequencia		= b.nr_seq_processo
	and	a.nr_sequencia 		= nr_sequencia_p
	and	((b.cd_perfil_lib	= cd_perfil_w) or (coalesce(b.cd_perfil_lib::text, '') = ''))
	and	((b.nm_usuario_lib	= nm_usuario_p) or (coalesce(b.nm_usuario_lib::text, '') = ''))  LIMIT 1;
	exception when others then
		ie_retorno_w := 'N';
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
