SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION qua_obter_se_rnc_usuario_envol ( nr_sequencia_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_resultado_w	varchar(01) := 'N';
qt_reg_w		bigint;


BEGIN
select	count(*)
into STRICT	qt_reg_w
from	qua_nao_conform_setor
where	nr_seq_nao_conform		= nr_sequencia_p;

/*Se nao tiver nenhuma regra, esta liberado*/

if (qt_reg_w = 0) then
	ds_resultado_w	:= 'N';
else
	/*Se tiver alguma regra em branco, esta liberado*/

	select	count(*)
	into STRICT	qt_reg_w
	from	qua_nao_conform_setor
	where	nr_seq_nao_conform	= nr_sequencia_p
	and	coalesce(cd_setor_atendimento::text, '') = ''
	and	coalesce(nr_seq_grupo_usuario_dest::text, '') = ''
	and	coalesce(nm_usuario_destino::text, '') = '';

	if (qt_reg_w > 0) then
		ds_resultado_w	:= 'S';
	end if;

	/*Se tiver regra para o usuario, esta liberado*/

	select	count(*)
	into STRICT	qt_reg_w
	from	qua_nao_conform_setor
	where	nr_seq_nao_conform	= nr_sequencia_p
	and	nm_usuario_destino		= nm_usuario_p;

	if (qt_reg_w > 0) then
		ds_resultado_w	:= 'S';
	end if;

	/*Se tiver regra para o setor do usu√°rio, esta liberado*/

	if (ds_resultado_w = 'N') then
		select	coalesce(max(obter_se_setor_usuario(cd_setor_atendimento, nm_usuario_p)), 'N')
		into STRICT	ds_resultado_w
		from	qua_nao_conform_setor
		where	nr_seq_nao_conform	= nr_sequencia_p
		and	(cd_setor_atendimento IS NOT NULL AND cd_setor_atendimento::text <> '');
	end if;
end if;

return	ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
