CREATE OR REPLACE FUNCTION qua_obter_se_subproc_doc_lib ( nr_sequencia_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'S';
qt_reg_w			bigint;
cd_setor_w		integer;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	qua_doc_subproc_emp a
where	a.nr_seq_doc = nr_sequencia_p
and exists (	SELECT	1
		from	qua_subprocesso_lib x
		where	x.nr_seq_subprocesso = a.nr_seq_subprocesso);

if (qt_reg_w > 0) then
	begin
	ie_retorno_w := 'N';

	begin
	select	cd_setor_atendimento
	into STRICT	cd_setor_w
	from	usuario
	where	nm_usuario = nm_usuario_p;
	exception
	when others then
		ie_retorno_w := 'S';
	end;

	if (ie_retorno_w = 'N') then
		begin
		select	'S'
		into STRICT	ie_retorno_w
		from	qua_subprocesso_lib b,
			qua_doc_subproc_emp a
		where	a.nr_seq_subprocesso	= b.nr_seq_subprocesso
		and	a.nr_seq_doc		= nr_sequencia_p
		and	b.cd_setor_lib		= cd_setor_w  LIMIT 1;
		exception
		when others then
			ie_retorno_w := 'N';
		end;
	end if;
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
