CREATE OR REPLACE FUNCTION qua_obter_se_tipo_causa_estab ( nr_sequencia_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'S';
qt_reg_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	qua_tipo_causa_estab
where	nr_seq_tipo_causa = nr_sequencia_p;

if (qt_reg_w > 0) then
	begin
	select	'S'
	into STRICT	ie_retorno_w
	from	qua_tipo_causa_estab
	where	nr_seq_tipo_causa = nr_sequencia_p
	and	cd_estabelecimento = cd_estabelecimento_p  LIMIT 1;
	exception
	when others then
		ie_retorno_w := 'N';
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
