CREATE OR REPLACE FUNCTION qua_obter_se_tipo_causa_lib ( nr_seq_tipo_causa_p bigint, nm_usuario_p text, nr_seq_nao_conform_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';
ie_regra_vis_w		varchar(1) := 'S';
qt_regras_w		integer;
cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;
cd_perfil_w		integer := obter_perfil_ativo;
nr_seq_tipo_nc_w		bigint;

c01 CURSOR FOR
SELECT	ie_visualiza
from	qua_tipo_causa_perfil
where	nr_seq_tipo_causa		= nr_seq_tipo_causa_p
and	coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w
and	coalesce(nm_usuario_lib,nm_usuario_p)= nm_usuario_p
order by coalesce(cd_perfil,0) desc,
	coalesce(nm_usuario_lib,'0') desc;


BEGIN

select	count(*)
into STRICT	qt_regras_w
from	qua_tipo_causa_estab
where	nr_seq_tipo_causa = nr_seq_tipo_causa_p;

if (qt_regras_w > 0) then
	begin
	select	'S'
	into STRICT	ds_retorno_w
	from	qua_tipo_causa_estab
	where	nr_seq_tipo_causa	= nr_seq_tipo_causa_p
	and	cd_estabelecimento	= cd_estabelecimento_w  LIMIT 1;
	exception
	when others then
		ds_retorno_w := 'N';
	end;
end if;

if (ds_retorno_w = 'S') then
	begin
	select	count(*)
	into STRICT	qt_regras_w
	from	qua_tipo_causa_perfil
	where	nr_seq_tipo_causa = nr_seq_tipo_causa_p;

	if (qt_regras_w > 0) then

		ie_regra_vis_w := 'N';

		open C01;
		loop
		fetch C01 into
			ie_regra_vis_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			ie_regra_vis_w := ie_regra_vis_w;
			end;
		end loop;
		close C01;
	end if;
	end;

	ds_retorno_w	:= ie_regra_vis_w;
end if;

if (ds_retorno_w = 'S') and (coalesce(nr_seq_nao_conform_p,0) > 0) then
	begin

	begin
	select	nr_seq_tipo
	into STRICT	nr_seq_tipo_nc_w
	from	qua_nao_conformidade
	where	nr_sequencia = nr_seq_nao_conform_p;
	exception
	when others then
		nr_seq_tipo_nc_w := 0;
	end;

	select	count(*)
	into STRICT	qt_regras_w
	from	qua_tipo_causa_tipo_nc
	where	nr_seq_tipo_causa = nr_seq_tipo_causa_p;

	if (nr_seq_tipo_nc_w > 0) and (qt_regras_w > 0) then
		begin
		select	'S'
		into STRICT	ds_retorno_w
		from	qua_tipo_causa_tipo_nc
		where	nr_seq_tipo_causa = nr_seq_tipo_causa_p
		and	nr_seq_tipo_nao_conform = nr_seq_tipo_nc_w  LIMIT 1;
		exception
		when others then
			ds_retorno_w := 'N';
		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
