CREATE OR REPLACE FUNCTION qua_obter_se_tipo_doc_estab ( nr_seq_tipo_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(01) := 'S';
qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	qua_tipo_doc_estab
where	nr_seq_tipo_doc = nr_seq_tipo_p;

if (qt_existe_w > 0) then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	qua_tipo_doc_estab
	where	nr_seq_tipo_doc 	= nr_seq_tipo_p
	and	cd_estabelecimento	= cd_estabelecimento_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
