CREATE OR REPLACE FUNCTION qua_obter_se_tipo_ref_doc_lib ( nr_sequencia_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'S';
qt_reg_w			bigint;
cd_perfil_w		integer := obter_perfil_ativo;

C01 CURSOR FOR
	SELECT	coalesce(ie_permite_visualizar,'S')
	from	qua_tipo_referencia_lib
	where	coalesce(nm_usuario_lib,nm_usuario_p) = nm_usuario_p
	and	coalesce(cd_perfil_lib,cd_perfil_w) = cd_perfil_w
	and	nr_seq_tipo_referencia = nr_sequencia_p
	and	ie_situacao = 'A'
	order by nm_usuario_lib desc, coalesce(cd_perfil_lib,0), nr_sequencia;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	qua_tipo_referencia_lib
where	ie_situacao = 'A'
and	nr_seq_tipo_referencia = nr_sequencia_p;

if (qt_reg_w > 0) then
	begin
	ie_retorno_w := 'N';

	open C01;
	loop
	fetch C01 into
		ie_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
