CREATE OR REPLACE FUNCTION qua_obter_tempo_nc ( dt_abertura_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
qt_mes_total_w			double precision	:= 0;


BEGIN

if (dt_abertura_p IS NOT NULL AND dt_abertura_p::text <> '') then
	begin
	qt_mes_total_w	:= coalesce(months_between(trunc(clock_timestamp(),'dd'),trunc(dt_abertura_p,'dd')),0);

	if (qt_mes_total_w <=3)	then
		ds_retorno_w	:=	substr(Wheb_mensagem_pck.get_texto(784418),1,255);--'0 a 3 meses';
	elsif (qt_mes_total_w > 3) and (qt_mes_total_w <= 6) then
		ds_retorno_w	:=	substr(Wheb_mensagem_pck.get_texto(784419),1,255);--'3 a 6 meses';
	elsif (qt_mes_total_w > 6) and (qt_mes_total_w <= 9) then
		ds_retorno_w	:=	substr(Wheb_mensagem_pck.get_texto(793177),1,255);--'6 a 9 meses';
	elsif (qt_mes_total_w > 9) and (qt_mes_total_w <=12) then
		ds_retorno_w	:=	substr(Wheb_mensagem_pck.get_texto(784422),1,255);--'9 a 12 meses';
	elsif (qt_mes_total_w > 12) then
		ds_retorno_w	:=	substr(Wheb_mensagem_pck.get_texto(784423),1,255);--'12 meses ou +';
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
