CREATE OR REPLACE FUNCTION qua_obter_total_analise_probl ( nr_seq_problema_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	bigint := 0;
qt_valor_w	integer;

c01 CURSOR FOR
SELECT	coalesce(qt_valor,1)
from	qua_analise_problema_crit
where	nr_seq_analise = nr_seq_problema_p
and	coalesce(qt_valor,0) > 0;


BEGIN
if (nr_seq_problema_p > 0) then
	open C01;
	loop
	fetch C01 into
		qt_valor_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (ds_retorno_w = 0) then
			ds_retorno_w := 1;
		end if;

		ds_retorno_w := ds_retorno_w * qt_valor_w;
		end;
	end loop;
	close C01;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
