CREATE OR REPLACE FUNCTION qua_obter_valor_item_audit ( nr_sequencia_p bigint, ie_estrutura_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_estrutura_p:
I - Item
E - Estrutura
C - Capitulo
T - Tipo

ie_opcao_p
'M' - MÃ©dia
'S' - Soma
*/
vl_item_w			double precision := 0;
qt_item_w		double precision := 0;
vl_media_w		double precision := 0;


BEGIN
if (coalesce(nr_sequencia_p,0) > 0) then
	begin
	if (ie_estrutura_p	= 'I') then
		select	coalesce(max(vl_item),0)
		into STRICT	vl_item_w
		from	qua_auditoria_item
		where	nr_sequencia	= nr_sequencia_p
		and	ie_permite_na	= 'N';
	elsif (ie_estrutura_p	= 'E') then
		if (ie_opcao_p = 'S') then
			select	coalesce(sum(vl_item),0)
			into STRICT	vl_item_w
			from	qua_auditoria_item
			where	nr_seq_estrutura		= nr_sequencia_p
			and	ie_permite_na		= 'N';
		else
			select	dividir(coalesce(sum(vl_item),0), count(*))
			into STRICT	vl_item_w
			from	qua_auditoria_item
			where	nr_seq_estrutura		= nr_sequencia_p
			and	ie_permite_na		= 'N';
		end if;
	elsif (ie_estrutura_p	= 'C') then
		if (ie_opcao_p = 'S') then
			select	coalesce(sum(a.vl_item),0)
			into STRICT	vl_item_w
			from	qua_auditoria_estrut b,
				qua_auditoria_item a
			where	a.nr_seq_estrutura		= b.nr_sequencia
			and	b.nr_seq_cap		= nr_sequencia_p
			and	a.ie_permite_na		= 'N';
		else
			select	dividir(coalesce(sum(a.vl_item),0), count(*))
			into STRICT	vl_item_w
			from	qua_auditoria_estrut b,
				qua_auditoria_item a
			where	a.nr_seq_estrutura		= b.nr_sequencia
			and	b.nr_seq_cap		= nr_sequencia_p
			and	a.ie_permite_na		= 'N';
		end if;
	elsif (ie_estrutura_p	= 'T') then
		if (ie_opcao_p = 'S') then
			select	coalesce(sum(a.vl_item),0)
			into STRICT	vl_item_w
			from	qua_auditoria_cap c,
				qua_auditoria_estrut b,
				qua_auditoria_item a
			where	a.nr_seq_estrutura		= b.nr_sequencia
			and	b.nr_seq_cap		= c.nr_sequencia
			and	c.nr_seq_tipo		= nr_sequencia_p
			and	a.ie_permite_na		= 'N';
		else
			select	dividir(coalesce(sum(a.vl_item),0), count(*))
			into STRICT	vl_item_w
			from	qua_auditoria_cap c,
				qua_auditoria_estrut b,
				qua_auditoria_item a
			where	a.nr_seq_estrutura		= b.nr_sequencia
			and	b.nr_seq_cap		= c.nr_sequencia
			and	c.nr_seq_tipo		= nr_sequencia_p
			and	a.ie_permite_na		= 'N';
		end if;
	end if;
	end;
end if;

return	vl_item_w;

end;
$body$
