CREATE OR REPLACE FUNCTION qua_ultimo_aprov_revisao ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		varchar(15);


BEGIN

select	max(nm_usuario_aprovacao)
into STRICT	ds_resultado_w
from	qua_doc_revisao
where	dt_aprovacao = (
	SELECT	max(dt_aprovacao)
	from	qua_doc_revisao
	where	nr_seq_doc = nr_sequencia_p);


RETURN ds_resultado_w;
END;
