CREATE OR REPLACE FUNCTION quebra_linha_etiqueta2 (ds_texto_p text, qt_linha_p bigint, qt_caracter_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);
ds_texto_w	varchar(4000);
ds_letra_w	varchar(1);

i		integer;
j		integer;
qt_caracter_w	integer;
ie_continua_w		boolean;


BEGIN
ds_texto_w	:= ds_texto_p||' ';
qt_caracter_w	:= qt_caracter_p+1;

if (qt_caracter_p > 0) then
	for i in 1..qt_linha_p loop
		begin
		ie_continua_w := true;
		ds_retorno_w	:= '';
		for j in 1..qt_caracter_w loop
			begin
			if (ie_continua_w) then
				ds_letra_w	:= substr(ds_texto_w,1,1);
				ds_texto_w	:= substr(ds_texto_w,2,length(ds_texto_w));

				if (ds_letra_w = ' ') then
					if (position(' ' in ds_texto_w) > (qt_caracter_w - j) or position(' ' in ds_texto_w) = 0) then
						ie_continua_w := false;
					else
						ds_retorno_w	:= ds_retorno_w||ds_letra_w;
					end if;

				elsif (length(ds_texto_w) = 0) then
					ie_continua_w := false;
				else
					ds_retorno_w	:= ds_retorno_w||ds_letra_w;
				end if;
			end if;
			end;
		end loop;
		end;
	end loop;
end if;

return	ds_retorno_w;

end;
$body$
