CREATE OR REPLACE FUNCTION que_obter_descricao ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_opcao_p
O = Descrição da opção (NR_SEQ_QUESTAO_OPCAO)
Q = Descrição da questão (NR_SEQ_QUESTAO)
*/
ds_retorno_w	varchar(255);


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	if (ie_opcao_p = 'O') then
		select	ds_opcao
		into STRICT	ds_retorno_w
		from	que_questao_opcao
		where	nr_sequencia = nr_sequencia_p;
	end if;

	if (ie_opcao_p = 'Q') then
		select	ds_questao
		into STRICT	ds_retorno_w
		from	que_questao
		where	nr_sequencia = nr_sequencia_p;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
