CREATE OR REPLACE FUNCTION que_obter_questao_original (nr_seq_questao_p bigint, qt_nivel_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_retorno_w	bigint;
qt_nivel_w		bigint;
ds_niveis_passados_w	varchar(255);


BEGIN
if (nr_seq_questao_p IS NOT NULL AND nr_seq_questao_p::text <> '') then
	qt_nivel_w	 := 0;
	nr_sequencia_w	 := nr_seq_questao_p;
	nr_seq_retorno_w := nr_sequencia_w;
	ds_niveis_passados_w :=  ',';

	while(qt_nivel_w <> qt_nivel_p) and (obter_se_contido(qt_nivel_w, ds_niveis_passados_w) = 'N') loop
	begin
		ds_niveis_passados_w := substr(ds_niveis_passados_w || qt_nivel_w || ',', 0, 255);

		select	max(nr_seq_questao_sup),
			max(qt_nivel-1)
		into STRICT	nr_sequencia_w,
			qt_nivel_w
		from	que_questao
		where	1 = 1
		and	nr_sequencia = nr_seq_retorno_w;

	nr_seq_retorno_w := nr_sequencia_w;
	end;
	end loop;

end if;

return	nr_seq_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
