SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION recep_obter_se_exame_autor (nr_seq_agenda_p bigint, nr_prescricao_p bigint, nr_seq_prescr_proc_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exame_autorizado_w	varchar(2);
nr_sequencia_w			bigint;
ie_estagio_w			varchar(2);


BEGIN

ie_exame_autorizado_w := 'N';

if (coalesce(nr_seq_agenda_p,0) > 0) then

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	autorizacao_convenio
	where	nr_seq_agenda	= nr_seq_agenda_p;


elsif (coalesce(nr_prescricao_p,0) > 0) then

	select	coalesce(max(a.nr_sequencia),0)
	into STRICT	nr_sequencia_w
	FROM autorizacao_convenio a
LEFT OUTER JOIN procedimento_autorizado b ON (a.nr_sequencia = b.nr_sequencia_autor)
WHERE b.nr_prescricao 		= nr_prescricao_p and b.nr_seq_prescricao		= nr_seq_prescr_proc_p;

end if;

if (nr_sequencia_w > 0) then

	select	a.ie_interno
	into STRICT	ie_estagio_w
	from  	estagio_autorizacao a,
			autorizacao_convenio b
	where 	b.nr_seq_estagio = a.nr_sequencia
	and	  	b.nr_sequencia = nr_sequencia_w;

	if (ie_estagio_w = '10') then
		ie_exame_autorizado_w := 'S';
	else
		ie_exame_autorizado_w := 'N';
	end if;

end if;

return	ie_exame_autorizado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
