CREATE OR REPLACE FUNCTION regra_gerar_cir_autorizacao (ie_autorizacao_p text) RETURNS varchar AS $body$
DECLARE


ie_possui_w		varchar(1);
ds_retorno_w		varchar(1) := 'S';


BEGIN

select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_possui_w
from   	status_agenda_gerar_cirur
where  	ie_autorizacao = ie_autorizacao_p;


if (ie_possui_w = 'S') then

	select coalesce(max('S'),'N')
	into STRICT   ds_retorno_w
	from   status_agenda_gerar_cirur
	where  ie_autorizacao = ie_autorizacao_p
	and    ie_permite_gerar_cirur = 'S';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
