CREATE OR REPLACE FUNCTION regra_lib_suep_complementar ( nr_atendimento_p bigint, nr_seq_suep_p bigint) RETURNS varchar AS $body$
DECLARE


ds_sql_not_in_regra_w     varchar(80) := '';
ds_sql_w                  varchar(4000);
customSQL_w               varchar(4000);
ie_resultado_w            varchar(02);
nr_sequencia_w            bigint;

C01 CURSOR FOR
	SELECT	a.ds_consulta,
            a.nr_sequencia
	from    regra_liberacao_suep a
	where   a.nr_seq_suep = nr_seq_suep_p
	and	    (a.ds_consulta IS NOT NULL AND a.ds_consulta::text <> '');


BEGIN
    ds_sql_not_in_regra_w := 'X';

    open C01;
    loop
    fetch C01 into	
        ds_sql_w,
        nr_sequencia_w;
    EXIT WHEN NOT FOUND; /* apply on C01 */
    begin

      customSQL_w :=  ' select	nvl(max(''S''),''N'') ' ||
                      ' from  	atendimento_paciente a ' ||
                      ' where  	a.nr_atendimento = :nr_atendimento ' ||
					  ds_sql_w;

      EXECUTE customSQL_w
      into STRICT   ie_resultado_w
      using  nr_atendimento_p;

      if ie_resultado_w = 'N' then
        
        if (length(ds_sql_not_in_regra_w) > 0) then
          ds_sql_not_in_regra_w := ds_sql_not_in_regra_w || ',';
        end if;

        ds_sql_not_in_regra_w := ds_sql_not_in_regra_w || nr_sequencia_w;

      end if;

    end;
    end loop;
    close C01;

    return	ds_sql_not_in_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
