CREATE OR REPLACE FUNCTION regra_mostra_fornecedor_consig ( cd_estabelecimento_p bigint, cd_cgc_p text, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


/*
Regra do dominio = 1384
1 = Mostrar todos os fornecedores
2 = Somente fornecedores com saldo de estoque
3 = Ordenar fornecedores pelo saldo de estoque
*/
ie_mostra_w			varchar(1);
ie_regra_fornec_consig_w	varchar(1);
qt_existe_w			integer;


BEGIN
ie_mostra_w		:= 'S';

select	coalesce(max(ie_regra_fornec_consig),1)
into STRICT	ie_regra_fornec_consig_w
from	parametro_estoque
where	cd_estabelecimento = cd_estabelecimento_p;
if (ie_regra_fornec_consig_w = 2) then

	select	count(*)
	into STRICT	qt_existe_w
	from (
		SELECT distinct a.cd_fornecedor
	from	pessoa_juridica b,
		fornecedor_mat_consignado a,
		material m
	where	a.cd_fornecedor = b.cd_cgc
	and a.cd_estabelecimento = cd_estabelecimento_p
	and	a.cd_material   = m.cd_material_estoque
	and	m.cd_material   = cd_material_p
	and	b.ie_situacao = 'A'
	and	a.qt_estoque > 0) x
	where	x.cd_fornecedor = cd_cgc_p;

	ie_mostra_w	:= 'N';	
	if (qt_existe_w > 0) then
		ie_mostra_w	:= 'S';
	end if;

end if;

return ie_mostra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
