CREATE OR REPLACE FUNCTION regra_permissao_itens_cirur (nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w	varchar(1);
dt_termino_w	timestamp;
qt_minutos_w	bigint;


BEGIN
ie_permite_w := 'S';

select 	max(dt_termino)
into STRICT	dt_termino_w
from 	cirurgia
where	nr_cirurgia = nr_cirurgia_p;

select	max(qt_minutos)
into STRICT	qt_minutos_w
from 	regra_perm_adic_mat_cir;

if	(clock_timestamp() > (dt_termino_w + qt_minutos_w/1440)) then
	ie_permite_w := 'N';
end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
