CREATE OR REPLACE FUNCTION regra_setor_triagem_risco ( cd_setor_p bigint, nr_seq_triagem_p bigint) RETURNS varchar AS $body$
DECLARE

ie_permitir_w  	varchar(1) := 'S';


BEGIN

	select 	 coalesce(MAX(b.ie_permite),'S')
	into STRICT     ie_permitir_w
	FROM triagem_classif_risco a
LEFT OUTER JOIN triagem_clas_risco_regra b ON (a.nr_sequencia = b.nr_seq_triagem)
WHERE a.nr_sequencia = nr_seq_triagem_p and b.cd_setor_atendimento  = cd_setor_p order by  coalesce(b.ie_permite,'S') desc;

return 	ie_permitir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
