SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION reg_doc_has_user_delegation ( nr_seq_document_p bigint, ie_reviewer_type_p text, nm_user_reviewer_p text, nm_user_delegation_p text ) RETURNS varchar AS $body$
DECLARE


ie_has_delegation_w		varchar(1);


BEGIN

select	CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END
into STRICT	ie_has_delegation_w
from	reg_documento doc,
	reg_doc_revisor dr,
	reg_doc_delegacao dd
where	doc.nr_sequencia = dr.nr_seq_documento
and	dr.nr_sequencia = dd.nr_seq_reg_doc_revisor
and	doc.nr_sequencia = nr_seq_document_p
and	dr.ie_tipo_revisor = ie_reviewer_type_p
and	dr.nm_usuario_revisor = nm_user_reviewer_p
and	dd.nm_usuario_delegacao = nm_user_delegation_p
and	(
		(coalesce(dd.dt_fim_vigencia::text, '') = '' and clock_timestamp() > dd.dt_inicio_vigencia) or (clock_timestamp() between dd.dt_inicio_vigencia and fim_dia(dd.dt_fim_vigencia))
	);

return ie_has_delegation_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
