CREATE OR REPLACE FUNCTION reg_get_ds_responsibles ( nr_seq_obj bigint, ds_obj_type text, ie_use_name text default 'S') RETURNS varchar AS $body$
DECLARE


	ds_table_name_w	varchar(30);
	ds_field_name_w	varchar(30);
	ds_output	varchar(4000);

	ds_user_field_w	varchar(70);


BEGIN
	if	ds_obj_type = 'CR' then
		ds_table_name_w := 'reg_resp_cr';
		ds_field_name_w := 'nr_seq_cr';
	elsif	ds_obj_type = 'FE' then
		ds_table_name_w := 'reg_resp_feature';
		ds_field_name_w := 'nr_seq_feature';
	elsif	ds_obj_type = 'GR' then
		ds_table_name_w := 'reg_resp_group';
		ds_field_name_w := 'nr_seq_group';
	end if;

	if	ie_use_name = 'S' then
		ds_user_field_w := 'obter_nome_usuario(nm_usuario_resp)||''(''||nm_usuario_resp||'')''';
	else
		ds_user_field_w := 'nm_usuario_resp';
	end if;

	ds_output := obter_valor_dinamico_4000_bv('select '||ds_user_field_w||' from '||ds_table_name_w||' where '||ds_field_name_w||' = :nr_sequencia', 'nr_sequencia='||nr_seq_obj, ds_output);

	return ds_output;
end;
