SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION reg_is_rev_hist_pend_user ( nr_seq_rev_hist_p bigint, nm_usuario_p text ) RETURNS varchar AS $body$
DECLARE


nm_author_w				reg_doc_revisor.nm_usuario_revisor%type;
ie_rev_hist_pending_w			varchar(1);


BEGIN

select	reg_fetch_revision_author(vr.nr_sequencia)
into STRICT	nm_author_w
from	reg_version_revision vr,
	reg_version_approvers va
where	vr.nr_sequencia = va.nr_seq_revision
and	va.nr_sequencia = nr_seq_rev_hist_p;

select	CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END
into STRICT	ie_rev_hist_pending_w
from	reg_version_revision vr,
	reg_version_approvers va
where	vr.nr_sequencia = va.nr_seq_revision
and	va.nr_sequencia = nr_seq_rev_hist_p
and	va.nr_seq_rev_control = (	SELECT	max(nr_sequencia)
					from	reg_revision_control
					where	nr_seq_revisao = vr.nr_sequencia
				)
and	(
		(
			(va.nm_approver = nm_usuario_p or reg_doc_has_user_delegation(vr.nr_seq_documento, va.ie_approver_type, va.nm_approver, nm_usuario_p) = 'S')
			and	(
					(
						va.ie_approver_type = 'R'
						and	(
								(vr.ie_status_revisao = 'R' and coalesce(va.ie_aceito::text, '') = '') or (vr.ie_status_revisao = 'S' and coalesce(va.dt_approved::text, '') = '')
							)
					) or -- Reviewer
					(
						va.ie_approver_type = 'A'
						and	vr.ie_status_revisao = 'P'
						and	coalesce(va.dt_approved::text, '') = ''
					) -- Approver
				)
		) or
		(
			(nm_author_w = nm_usuario_p or reg_doc_has_user_delegation(vr.nr_seq_documento, 'C', nm_author_w, nm_usuario_p) = 'S')
			and	vr.ie_status_revisao = 'A'
			and	va.ie_aceito = 'N'
			and	coalesce(va.dt_comentario_autor::text, '') = ''
		)
	);

return ie_rev_hist_pending_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
