CREATE OR REPLACE FUNCTION reg_obter_complex_leito (cd_tipo_leito_p text, ie_texto_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);

BEGIN
	if (cd_tipo_leito_p IS NOT NULL AND cd_tipo_leito_p::text <> '') then

		if (ie_texto_p = 'N') then
			select nr_ordem
			  into STRICT ds_retorno_w
			  from regulacao_tipo_leito
			 where cd_tipo_leito = cd_tipo_leito_p;
	        else
			select CASE WHEN nr_ordem='1' THEN  'ALTA'  ELSE 'BAIXA' END
			  into STRICT ds_retorno_w
			  from regulacao_tipo_leito
			 where cd_tipo_leito = cd_tipo_leito_p;
		end if;
	end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
