SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION reg_obter_desc_interno (nm_tabela_p text, cd_interno_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);

BEGIN
if	(nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '' AND cd_interno_p IS NOT NULL AND cd_interno_p::text <> '') then 
 
	if (nm_tabela_p = 'MOTIVO_ALTA') then 
	 
		select	max(ds_motivo_alta) 
		into STRICT	ds_retorno_w 
		from	motivo_alta 
		where	cd_motivo_alta = cd_interno_p;
			 
	elsif (nm_tabela_p = 'CLASSIF_UNIDADE_ATEND') then 
	 
		select	max(ds_classificacao) 
		into STRICT	ds_retorno_w 
		from	classif_unidade_atend 
		where	nr_sequencia = (cd_interno_p)::numeric;
			 
	elsif (nm_tabela_p = 'ESPECIALIDADE_LEITO') then 
	 
		select	max(ds_especialidade) 
		into STRICT	ds_retorno_w 
		from	especialidade_medica 
		where	cd_especialidade = cd_interno_p;
			 
	elsif (nm_tabela_p = 'MOTIVO_TRANSFERENCIA') then 
	 
		select	max(ds_motivo) 
		into STRICT	ds_retorno_w 
		from	motivo_transf_pac 
		where	nr_sequencia = (cd_interno_p)::numeric;
	elsif (nm_tabela_p = 'MEDICO_REGULACAO') then 
		select substr(obter_nome_pf(cd_interno_p),1,255) 
		into STRICT ds_retorno_w 
		;				
	end if;
 
end if;
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION reg_obter_desc_interno (nm_tabela_p text, cd_interno_p text) FROM PUBLIC;

