CREATE OR REPLACE FUNCTION reg_obter_nome_prof ( cd_profissional_p text) RETURNS varchar AS $body$
DECLARE

			 
nm_profissional_w	varchar(60);
			

BEGIN 
if (cd_profissional_p IS NOT NULL AND cd_profissional_p::text <> '') then 
	 
	select	max(substr(obter_nome_pf(b.cd_interno),1,60)) 
	into STRICT	nm_profissional_w 
	from	conversao_meio_externo b 
	where	 b.ie_sistema_externo = 'REG' 
	and	upper(b.nm_tabela) = 'MEDICO_REGULACAO' 
	and	upper(b.nm_atributo) = 'CD_PROFISSIONAL' 
	and	upper(b.cd_interno) = upper(cd_profissional_p);
end if;
 
return	nm_profissional_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
