CREATE OR REPLACE FUNCTION remove_dias_aplicacao (ds_dias_p text, qt_dias_p bigint) RETURNS varchar AS $body$
DECLARE


ds_dias_w		varchar(4000) := '';
cont_w			bigint;
posicao_w		smallint;


BEGIN

cont_w := 0;
ds_dias_w := ds_dias_p;
while (ds_dias_w IS NOT NULL AND ds_dias_w::text <> '') loop
	begin
	if (cont_w = qt_dias_p) then
		exit;
	end if;
	posicao_w	:= position(',' in ds_dias_w);
	ds_dias_w	:= substr(ds_dias_w, posicao_w + 1, length(ds_dias_w));
	cont_w		:= cont_w + 1;
	end;
end loop;

return ds_dias_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
