CREATE OR REPLACE FUNCTION replace_clob ( ds_valor_p text, ds_localizar_p text, ds_substituir_p text ) RETURNS text AS $body$
DECLARE


  tam_localizar_w       CONSTANT integer := coalesce(LENGTH(ds_localizar_p),0);
  tam_substituir_w      CONSTANT integer := coalesce(LENGTH(ds_substituir_p),0);

  return_w        text;
  segment_w       text;
  pos_w           integer := 1-tam_substituir_w;
  offset_w        integer := 1;


BEGIN

  IF (ds_localizar_p IS NOT NULL AND ds_localizar_p::text <> '') THEN
    WHILE offset_w < octet_length(ds_valor_p) LOOP
      segment_w := substr(ds_valor_p,32767,offset_w);
      LOOP
        pos_w := DBMS_LOB.INSTR(segment_w,ds_localizar_p,pos_w+tam_substituir_w);
        EXIT WHEN(coalesce(pos_w,0) = 0) OR (pos_w = 32767-tam_substituir_w);
        segment_w := ( substr(segment_w,pos_w-1)
                            ||ds_substituir_p
                            ||substr(segment_w,32767-tam_localizar_w-pos_w-tam_localizar_w+1,pos_w+tam_localizar_w));
      END LOOP;
      return_w := return_w||segment_w;
      offset_w := offset_w + 32767 - tam_localizar_w;
    END LOOP;
  END IF;

  RETURN(return_w);

END;
$body$
