SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION replace_number_locale (qt_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(200);
nls_numeric_characters_w	varchar(255);


BEGIN

begin
	select 	max(value)
	into STRICT	nls_numeric_characters_w
	from 	v$nls_parameters
	where	parameter = 'NLS_NUMERIC_CHARACTERS';
exception
when others then
	nls_numeric_characters_w	:= null;
end;

if (nls_numeric_characters_w <> '.,') then
	ds_retorno_w	:= replace(qt_p,'.',',');
else
	ds_retorno_w	:= qt_p;
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
