CREATE OR REPLACE FUNCTION replace_obs_disp (ds_observacao_p text) RETURNS varchar AS $body$
DECLARE


ds_observacao_w	varchar(4000);
expressao_w		bigint;
expressao_ww	bigint;
BEGIN

ds_observacao_w := ds_observacao_p;
expressao_w := 1114328;

for i in 1..3 loop
	begin
		ds_observacao_w := replace(ds_observacao_w, wheb_mensagem_pck.get_texto(1114325,'IE_REGRA_DISP='||wheb_mensagem_pck.get_texto(expressao_w)), '');
	end;

	expressao_ww := 1114328;

	for i in 1..3 loop
		begin
			ds_observacao_w := replace(ds_observacao_w, wheb_mensagem_pck.get_texto(1114689,'IE_ANT_REGRA_DISP='||wheb_mensagem_pck.get_texto(expressao_w)||
																							';IE_NOVA_REGRA_DISP='||wheb_mensagem_pck.get_texto(expressao_ww)), '');
		end;

		expressao_ww := expressao_ww + 1;
	end loop;

	expressao_w := expressao_w + 1;
end loop;

return	ds_observacao_w;

end;
