SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rep_obter_custo_proc (nr_prescricao_p bigint, nr_sequencia_p bigint, ie_opcao_p text, ie_informacao_p text) RETURNS bigint AS $body$
DECLARE

 
vl_unit_w		double precision;
vl_total_w		double precision;
vl_retorno_w		double precision;
			

BEGIN 
 
if (ie_opcao_p	= 'J') then 
 
	SELECT (obter_preco_procedimento(b.cd_estabelecimento, obter_convenio_atendimento(b.nr_atendimento), 
					obter_categoria_atendimento(b.nr_atendimento), TRUNC(a.dt_prev_execucao,'dd'), a.cd_procedimento, a.ie_origem_proced, NULL, NULL, b.cd_setor_atendimento, NULL, NULL, NULL, NULL, NULL, NULL, 'P')) vl_unit, 
		(a.qt_procedimento * obter_preco_procedimento(b.cd_estabelecimento, obter_convenio_atendimento(b.nr_atendimento), 
					obter_categoria_atendimento(b.nr_atendimento), TRUNC(a.dt_prev_execucao,'dd'), a.cd_procedimento, a.ie_origem_proced, NULL, NULL, b.cd_setor_atendimento, NULL, NULL, NULL, NULL, NULL, NULL, 'P')) vl_total 
	into STRICT	vl_unit_w, 
		vl_total_w 
	FROM	prescr_procedimento a, 
		prescr_medica b 
	WHERE	a.nr_prescricao = b.nr_prescricao 
	AND	b.nr_prescricao = nr_prescricao_p 
	and	a.nr_sequencia	= nr_sequencia_p;
	 
	if (ie_informacao_p = 'U') then 
		vl_retorno_w	:= vl_unit_w;
	elsif (ie_informacao_p = 'T') then 
		vl_retorno_w	:= vl_total_w;
	end if;
 
end if;
 
return	coalesce(vl_retorno_w,0);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
