SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rep_obter_dados_atend_pac ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
 
ds_retorno_w	varchar(255);
/* 
P - possui conta encerrada; 
DPA - data previsto alta; 
DAM - data alta medico 
*/
BEGIN 
if (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	begin 
	if (ie_opcao_p = 'P') then 
		begin 
		select	coalesce(max('S'),'N') 
		into STRICT	ds_retorno_w 
		from	atendimento_paciente where		(dt_fim_conta IS NOT NULL AND dt_fim_conta::text <> '') 
		and		nr_atendimento = nr_atendimento_p LIMIT 1;
		end;
	elsif (ie_opcao_p = 'DPA') then 
		begin 
		select	to_char(max(dt_previsto_alta),'dd/mm/yyyy hh24:mi:ss') 
		into STRICT	ds_retorno_w 
		from	atendimento_paciente 
		where	nr_atendimento 		= nr_atendimento_p 
		and		ie_probabilidade_alta 	= 'C';
		end;
	elsif (ie_opcao_p = 'DPAA') then 
		begin 
		ds_retorno_w	:= to_char(obter_data_prev_alta(nr_atendimento_p),'dd/mm/yyyy hh24:mi:ss');
		end;
	elsif (ie_opcao_p = 'DAM') then 
		begin 
		select	to_char(max(dt_alta_medico),'dd/mm/yyyy hh24:mi:ss') 
		into STRICT	ds_retorno_w 
		from	atendimento_paciente 
		where	nr_atendimento		= nr_atendimento_p;
		end;
	end if;
	end;
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
