SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rep_obter_relatorio_regra_imp ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_relatorio_w		bigint;
cd_relatorio_ww		varchar(2000);
cd_relatorio_ant_w	bigint;
cd_area_proced_w	bigint;
cd_grupo_proced_w	bigint;
cd_espec_proced_w	bigint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
cd_tipo_procedimento_w	bigint;
nr_seq_proc_interno_w	bigint;
		
C01 CURSOR FOR 
	SELECT	cd_procedimento, 
		ie_origem_proced, 
		nr_seq_proc_interno 
	from	prescr_procedimento 
	where	nr_prescricao = nr_prescricao_p;
		
C02 CURSOR FOR 
	SELECT	substr(obter_codigo_relatorio(a.nr_seq_relatorio),1,30) cd_relatorio 
	from	rep_regra_relatorio a, 
		rep_regra_relatorio_exame b 
	where	a.nr_sequencia = b.nr_seq_regra 
	and	((b.cd_procedimento = cd_procedimento_w)	  or (coalesce(b.cd_procedimento::text, '') = '')) 
	and	((b.ie_origem_proced = ie_origem_proced_w)	  or (coalesce(b.cd_procedimento::text, '') = '')) 
	and	((b.cd_area_procedimento = cd_area_proced_w)  	  or (coalesce(b.cd_area_procedimento::text, '') = '')) 
	and	((b.cd_especialidade = cd_espec_proced_w)	  or (coalesce(b.cd_especialidade::text, '') = '')) 
	and	((b.cd_tipo_procedimento = cd_tipo_procedimento_w) or (coalesce(b.cd_tipo_procedimento::text, '') = '')) 
	and	((b.cd_grupo_proc = cd_grupo_proced_w)		  or (coalesce(b.cd_grupo_proc::text, '') = '')) 
	and	((coalesce(b.nr_seq_proc_interno::text, '') = '') or (b.nr_seq_proc_interno = nr_seq_proc_interno_w)) 
	ORDER BY cd_relatorio;
		

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	cd_procedimento_w, 
	ie_origem_proced_w, 
	nr_seq_proc_interno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	 
	select	coalesce(max(cd_area_procedimento),0), 
		coalesce(max(cd_especialidade),0), 
		coalesce(max(cd_grupo_proc),0), 
		max(cd_tipo_procedimento) 
	into STRICT	cd_area_proced_w, 
		cd_espec_proced_w, 
		cd_grupo_proced_w, 
		cd_tipo_procedimento_w 
	from	estrutura_procedimento_v 
	where	cd_procedimento		= cd_procedimento_w 
	and	ie_origem_proced	= ie_origem_proced_w;
	 
	open C02;
	loop 
	fetch C02 into	 
		cd_relatorio_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		if (cd_relatorio_ant_w <> cd_relatorio_w) or (coalesce(cd_relatorio_ant_w::text, '') = '') then 
			cd_relatorio_ww := cd_relatorio_ww || cd_relatorio_w || ', ';
		end if;
		cd_relatorio_ant_w := cd_relatorio_w;
	end loop;
	close C02;
	 
end loop;
close C01;
 
cd_relatorio_ww := substr(cd_relatorio_ww,1,length(trim(both cd_relatorio_ww))-1);
 
return	cd_relatorio_ww;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
