CREATE OR REPLACE FUNCTION rep_obter_setor_atend_data ( nr_atendimento_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE



cd_setor_atendimento_w		bigint;


BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then
	begin

	select	coalesce(max(cd_setor_atendimento),0)
	into STRICT	cd_setor_atendimento_w
	from 	atend_paciente_unidade a
	where	a.nr_atendimento 		= nr_atendimento_p
	  and 	nr_seq_interno	=
		(SELECT max(coalesce(nr_seq_interno,0))
		from 	Setor_Atendimento y,
			atend_paciente_unidade b
		where nr_atendimento 	= nr_atendimento_p
		  and 	b.cd_setor_atendimento	= y.cd_setor_atendimento
		  and	b.dt_entrada_unidade <= dt_referencia_p
		  and 	y.cd_classif_setor in (1,3,4,8)); /* alterado por marcus em 16/4/2005  inclui 1 */
	end;
end if;

return cd_setor_atendimento_w;

end;
