CREATE OR REPLACE FUNCTION rep_obter_se_itens_iguais (nr_prescricao_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


cd_material_w		integer;
ie_consistido_w		varchar(1) := 'N';
nr_seq_regra_w		bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from 	rep_medicamento_efeito
	where	cd_material		= cd_material_w
	or 	cd_material_igual	= cd_material_w;

BEGIN

select	max(cd_material)
into STRICT	cd_material_w
from	prescr_material
where	nr_prescricao	= nr_prescricao_p
and	nr_sequencia	= nr_seq_item_p;

if (cd_material_w IS NOT NULL AND cd_material_w::text <> '') then
	open C01;
	loop
	fetch C01 into
		nr_seq_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		Select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_consistido_w
		from	prescr_material b,
			rep_medicamento_efeito a
		where	b.nr_prescricao	= nr_prescricao_p
		and (b.cd_material	= a.cd_material_igual or
			b.cd_material	= a.cd_material)
		and 	b.nr_sequencia	<> nr_seq_item_p
		and 	a.nr_sequencia	= nr_seq_regra_w;

		if	ie_consistido_w = 'S' then
			return ie_consistido_w;
		end if;
		end;
	end loop;
	close C01;
end if;

return	ie_consistido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
