CREATE OR REPLACE FUNCTION rep_obter_se_obriga_just_int ( ds_severidade_p text, cd_api_p alerta_api.cd_api%type default null, ds_sub_tipo_api_p alerta_api.ds_sub_tipo_api%type default null) RETURNS varchar AS $body$
DECLARE


	ie_severidade_api_w		varchar(1) := 'Y';
	ie_severidade_param_w	varchar(1) := 'X';
	ds_return_w				varchar(1) := 'N';


BEGIN

	if ((cd_api_p = 'A') and (ds_sub_tipo_api_p in ('FOOD', 'ALCOHOL'))) then
		ds_return_w := 'N';
	else
		if (ds_severidade_p IS NOT NULL AND ds_severidade_p::text <> '') then
			case
				when ds_severidade_p = 'CONTRAINDICATED' then
					ie_severidade_api_w := 'I';
				when ds_severidade_p = 'MINOR' then
					ie_severidade_api_w := 'L';
				when ds_severidade_p = 'MODERATE' then
					ie_severidade_api_w := 'M';
				when ds_severidade_p = 'MAJOR' then
					ie_severidade_api_w := 'S';
			end case;
		end if;

		ie_severidade_param_w := obter_param_usuario(924, 215, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_severidade_param_w);

		if (ie_severidade_param_w = 'L') then
			ds_return_w := 'S';
		elsif ((ie_severidade_param_w = 'M') and ((ie_severidade_api_w = 'M') or (ie_severidade_api_w = 'S')) or (ie_severidade_api_w = 'I')) then
			ds_return_w := 'S';
		elsif ((ie_severidade_param_w = 'S') and ((ie_severidade_api_w = 'S') or (ie_severidade_api_w = 'I'))) then
			ds_return_w := 'S';
		elsif (ie_severidade_param_w = 'I' AND ie_severidade_api_w = 'I') then
			ds_return_w := 'S';
		elsif (ie_severidade_api_w = ie_severidade_param_w) then
			ds_return_w := 'S';
		end if;
	end if;

	return ds_return_w;

end;
$body$
