CREATE OR REPLACE FUNCTION rep_obter_se_setor_lib ( nr_atendimento_p atendimento_paciente.nr_atendimento%type, nm_usuario_p usuario.nm_usuario%type, cd_perfil_p perfil.cd_perfil%type ) RETURNS varchar AS $body$
DECLARE


	ie_setor_atendimento_w	varchar(1);
	ie_unidade_atend_w		varchar(1);
	ie_retorno_w			varchar(1) := 'S';


BEGIN

	select	coalesce(max('S'),'N')
	into STRICT	ie_setor_atendimento_w
	from	usuario_setor_v b,
			atend_paciente_unidade a
	where	a.nr_atendimento = nr_atendimento_p
	and		a.cd_setor_atendimento = b.cd_setor_atendimento
	and		b.nm_usuario = nm_usuario_p;

	select	coalesce(max('S'),'N')
	into STRICT	ie_unidade_atend_w
	from	atend_paciente_unidade
	where	nr_atendimento = nr_atendimento_p;

	if (ie_setor_atendimento_w = 'N') and (ie_unidade_atend_w = 'S') then

		ie_retorno_w := 'N';

	end if;

	return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
