CREATE OR REPLACE FUNCTION rep_obter_todos_relat_regra () RETURNS varchar AS $body$
DECLARE

 
cd_relatorio_w		bigint;
cd_relatorio_ant_w	bigint;
ds_lista_relat_w	varchar(2000);

C01 CURSOR FOR 
	SELECT	substr(obter_codigo_relatorio(nr_seq_relatorio),1,30) 
	from	rep_regra_relatorio;

BEGIN
 
open C01;
loop 
fetch C01 into	 
	cd_relatorio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	if (cd_relatorio_ant_w <> cd_relatorio_w) or (coalesce(cd_relatorio_ant_w::text, '') = '') then 
		ds_lista_relat_w := ds_lista_relat_w || cd_relatorio_w || ', ';
	end if;
	cd_relatorio_ant_w := cd_relatorio_w;
end loop;
close C01;
 
ds_lista_relat_w := substr(ds_lista_relat_w,1,length(trim(both ds_lista_relat_w))-1);
 
return	ds_lista_relat_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
