SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION retirar_inconsistencia_lista (ds_inconsistencia_p text, cd_inconsistencia_p text) RETURNS varchar AS $body$
DECLARE


cd_inconsistencia_w	varchar(50)	:= '';
ds_inconsistencia_w	varchar(2000)	:= '';
ds_retorno_w		varchar(2000)	:= '';
i			integer;


BEGIN

ds_inconsistencia_w	:= ds_inconsistencia_p || ' ';
cd_inconsistencia_w 	:= '';

if (length(ds_inconsistencia_p) > 0) then
	begin
	for	i in 1..length(ds_inconsistencia_w) loop
		begin

		if (substr(ds_inconsistencia_w, i, 1) <> ' ') then
			cd_inconsistencia_w	:= cd_inconsistencia_w  || substr(ds_inconsistencia_w, i, 1);
		else
			begin

			if (replace(cd_inconsistencia_w, ' ', '') = cd_inconsistencia_p) then
				cd_inconsistencia_w 	:= 'X';
			end if;

			if (replace(cd_inconsistencia_w, ' ', '') <> 'X') then
				begin
				ds_retorno_w		:= ds_retorno_w || cd_inconsistencia_w || ' ';
				cd_inconsistencia_w	:= '';
				end;
			else
				cd_inconsistencia_w	:= '';
			end if;


			end;
		end if;

		end;
	end loop;
	end;
end if;

return substr(ds_retorno_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
