CREATE OR REPLACE FUNCTION retirar_inconsistencia (ds_inconsistencia_p text, cd_inconsistencia_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w       varchar(2000);


BEGIN

ds_retorno_w       := substr(ds_inconsistencia_p,1,255);

if (position(cd_inconsistencia_p in ds_retorno_w) = 1) then
   ds_retorno_w    := substr(ds_retorno_w, length(cd_inconsistencia_p) + 2, 255);
elsif (position(' ' || cd_inconsistencia_p || ' ' in ds_retorno_w) > 0) then
   ds_retorno_w    := replace(ds_retorno_w, ' '|| cd_inconsistencia_p || ' ', ' ');
end if;

return substr(ds_retorno_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
